import React, {Component} from 'react';
import WorkoutList from './WorkoutList/WorkoutList'

class WorkoutContainer extends Component {
    constructor(){
        super();
        this.state = {
            workouts: [],
            workoutToEdit: {
                _id: null,
                name: '',
                intervalOne: 0,
                intervalTwo: 0,
                cycles: 0
            },
            modalShowing: false
        }
    }

    componentDidMount(){
        this.getWorkouts();
    }

    getWorkouts = async () => {
        console.log("workouts function hit");
        try {
            const response = await fetch('http://localhost:9000/workouts');

            if(response.status !== 200){
                throw(Error(response.statusText));
            }

            const parsedWorkouts = await response.json();
            this.setState({
                workouts: parsedWorkouts.data
            })

        } catch(err) {
            console.log(err);
        }

    }

    createWorkout = async (formData, e) => {
        e.preventDefault();
        try {
            const createdWorkout = await fetch('http://localhost:9000/workouts', {
                method: 'POST',
                body: JSON.stringify(formData),
                headers: {
                    'Content-Type': 'application/json'
                }
            });

            const parsedResponse = await createdWorkout.json();
            this.setState({workouts: [...this.state.workouts, parsedResponse.data]})

        } catch(err) {
            console.log(err)
        }
    }

    deleteWorkout = (e) => {
        e.preventDefault();
        console.log('hit delete function');


    };

    editWorkout = (e) => {
        e.preventDefault();
        console.log(formData)


    }

    render(){
        return(
            <div>
                <h1>WorkoutContainer</h1>
                <WorkoutList editWorkout={this.editWorkout} workouts={this.state.workouts} createWorkout={this.createWorkout} deleteWorkout={this.deleteWorkout} /> 
            </div>

        )

    }
}


export default WorkoutContainer;
