import React from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

class EditWorkout extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        modal: false,
        name: '',
        intervalOne: null,
        intervalTwo: null,
        cycles: null
    };

    this.toggle = this.toggle.bind(this);
  }

  updateState = (e) =>{
        e.preventDefault();
        this.setState({
            [e.currentTarget.name]: e.currentTarget.value
        })
        console.log(this.state)
    }

  toggle() {
    this.setState(prevState => ({
      modal: !prevState.modal
    }));
  }

  render() {
    return (
      <div>

        <Button color="danger" onClick={this.toggle}>{this.props.buttonLabel}</Button>
        <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className}>
          <ModalHeader toggle={this.toggle}>Edit your workout!</ModalHeader>


          <ModalBody>
                <form onSubmit={this.props.editWorkout}>
                    name: <input onChange={this.updateState} type="text" name="name"/><br/>
                    intervalOne: <input onChange={this.updateState} type="text" name="intervalOne"/><br/>
                    intervalTwo: <input onChange={this.updateState} type="text" name="intervalTwo"/><br/>
                    cycles: <input onChange={this.updateState} type="text" name="cycles"/><br/>
                    <input type="submit"/>
                </form>
          </ModalBody>
          <ModalFooter>
            <Button type="submit" color="primary" onClick={this.toggle}>Let's Go!</Button>
            <Button color="secondary" onClick={this.toggle}>Cancel</Button>
          </ModalFooter>

        </Modal>

      </div>
    );
  }
}

export default EditWorkout;
